import axios from 'axios';
import { Route, Link } from 'react-router-dom'
import './App.css';
import { useEffect, useState } from 'react'
import ShowPlaylist from './component/showPlaylist'
import Post from './component/Post'


const API_URL ='https://api.airtable.com/v0/app89uih6eVWpafTK/Table%201?api_key=keyZHnK73JWzkEwP6'

function App() {

  const [song, setSong] =useState('')

  const [artists, setArtists] = useState([]);
  const [toggleFetch, setToggleFetch] = useState(true);


  useEffect(() => {

  const getSongs = async () => {
    const resp = await axios.get(API_URL);
    console.log(resp.data);
    setArtists(resp.data.records)
  }
    getSongs();

  },[toggleFetch] )


  return (
    <div>
      <nav>
        <Link to="/"> Home</Link> 

        <Link to="/about"> About</Link>
        <Link to="/playlist">Check out Playlist</Link>
      </nav>
      <br/>
      <body>
        <Route path='/' exact>
        <Post/>
        </Route>

        <Route path="/playlist">

          {artists.map((artist) => (

            <ShowPlaylist
              key={artist.id}
              artist={artist}
              song={song}
            />

          ))}

            </Route>
      </body>
        <br/>


    </div>
  );
}

export default App;
